graphql.query:
query countries($filter: CountryFilterInput, $pagination: PaginationInput) {
  countries(filter: $filter, page: $pagination) {
    totalCount
    edges {
      cursor
      node {
        id
        name
        iso3
        emoji
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
      endCursor
      startCursor
    }
  }
}

query country ($pagination: PaginationInput){
  country(iso3: "{{paisISO3}}") {
    # Country Fields
    id
    name
    iso2
    capital
    tld
    states(page: $pagination) {
      totalCount
      edges {
        cursor
        node {
          id
          name
        }
      }
    }
    currency
    currency_symbol
    emoji
    # ...
  }
}

query states($filter: StateFilterInput, $pagination: PaginationInput) {
  states(filter: $filter, page: $pagination) {
    totalCount
    edges {
      cursor
      node {
        name
        state_code
        country_code
        latitude
        longitude
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
      endCursor
      startCursor
    }
  }
}

query state($locationCode: StateCountryCodeInput ) {
state(locationCode: $locationCode){
  id
  name
  country_id
  country_code
  state_code
  cities(page: { first: 4 }) {
      totalCount
      edges {
        cursor
        node {
          id
          name
        }
      }
	}
}
}

